import React, {useState, useEffect} from 'react';
import { Card, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import {getQuestions} from './../../store/actions/questionActions';
const QuizPage = () => {
    const dispatch = useDispatch();
    const questions = useSelector(state => state.questionsReducer.questions);
    const [question, setQuestion] = useState({});
    const [choice, setChoice] = useState(null);
    useEffect(() => {
       dispatch(getQuestions()); 
    }, []);

    useEffect(() => { console.log(questions);
        if(questions){
            setQuestion(questions[0]);  console.log('heeeeeeeey');
        }
    }, [questions]);

    const nextQuestion = (evt) => {
        let index = questions.findIndex(item=>item==question);
        console.log(evt.target.value);
        setChoice(evt.target.value);
        setQuestion(questions[++index]);
    };
    return (
        <Card style={{textAlign: 'center'}}>
            <Card.Title>
               {question.category}
            </Card.Title>
            <Card.Body>
            <div style={{border: '3px solid red',width: '60vw', height: '30vw', margin: '0 auto'}}>
                <h3>{question?.question?.replace(/&quot;/g, '"')}</h3> 
                </div>
                <div key={`true`} className="mt-3">
          <Form.Check inline
          name="group1"
            type={'radio'}
            id={`true`}
            label={`True`}
            onChange={nextQuestion}
            checked={choice}

          />

          <Form.Check  inline
          name="group1"
            type={'radio'}
            id={`false`}
            label={`False`}
            onChange={/* nextQuestion */evt=>console.log(evt.target.id)}
            checked={choice}
          />
            </div>
            </Card.Body>

        </Card>
    );
};

export default QuizPage;
