import React, {useState, useEffect} from 'react';
import { Card, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
const ResultPage = ({answers, score}) => {
    return (
        <Card style={{textAlign: 'center', paddingTop: '20px'}}>
            <Card.Title>
               You scored {score} / 10
            </Card.Title>
            <Card.Body>
            <div style={{border: '3px solid red',width: '60vw', height: '50vh', margin: '0 auto'}}>
    {answers.map(answer=>(<p style={{color: answer.user_success?'green':'red'}}>{answer.question} {`'Corret Answer ${answer.correct_answer} `}</p>))}
                </div>
            </Card.Body>
        </Card>
    )
}

export default ResultPage;
