import React, {useEffect, useState} from 'react';
import {getArticle} from '../../store/actions/articlesActions';
import { useDispatch, useSelector } from 'react-redux';
import { Rating } from 'react-simple-star-rating'
import './ArticleView.css';
import { Card } from 'react-bootstrap';
const ArticleView = ({match}) => {
    const dispatch = useDispatch();
    const article = useSelector(state => state.articlesReducer.article);
    const [rating1, setrating1] = useState(article?.totalRates);
    const [rating2, setrating2] = useState(0);

    const handleRating1 = (rate: number) => {
      setrating1(rate);
    }
    const handleRating2 = (rate: number) => {
      setrating2(rate);
    }
useEffect(() => { 
   dispatch(getArticle(match.params.id));
}, [])

    return (
      <div>
        <article>
            {/* {article?article.body:null} */}
            <div className="vh-100 dt w-100 tc bg-dark-gray white cover" style={{background:`url('http://mrmrs.github.io/photos/u/009.jpg') no-repeat center`}}>
    <div className="dtc v-mid">
      {/* <header className="white-70">
        <h2 className="f6 fw1 ttu tracked mb2 lh-title">Issue Six</h2>
        <h3 className="f6 fw1 lh-title">Summer MMXVI</h3>
      </header> */}
      <h1 className="f1 f-headline-l fw1 i white-60">The Chronicles</h1>
      <blockquote className="ph0 mh0 measure f4 lh-copy center">
        <p className="fw1 white-70">
         {article.date}
        </p>
        <cite className="f6 ttu tracked fs-normal">{article.authorName}</cite>
      </blockquote>
      <Rating ratingValue={article?.totalRates*10} allowHalfIcon={true} transition={false} readonly={true} />
    <p className="fw1 white-70"> This article has a {article.totalRates} rating with {article.rates} votes</p>
    </div>
  </div>
  <Card className="container mt-2">
  <div class=" measure-wide f5 pv5 lh-copy ph2">
    <h1 class="f1 lh-title">The repercussion of ugliness is endless</h1>
    {article.body?.toString().split('\n\n').map(x=>(<p className="f4">{x}</p>))}

  </div>
  </Card>

        </article>
        <div className="container"> 
        <h3 className="fw1 f1">
          Rate Article
        </h3>
  <Rating onClick={handleRating2} ratingValue={rating2} allowHalfIcon={true} transition={false}  />

  </div>
        </div>
    )
}

export default ArticleView;
