import React, {useState, useEffect} from 'react';
import { Card, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import {getQuestions} from './../../store/actions/questionActions';
const QuizPage = () => {
    const dispatch = useDispatch();
    const questions = useSelector(state => state.questionsReducer.questions);
    const [question, setQuestion] = useState({});
    useEffect(() => {
       dispatch(getQuestions()); 
    }, []);

    useEffect(() => { console.log(questions);
        if(questions){
            setQuestion(questions[0]);  console.log('heeeeeeeey');
        }
    }, [questions]);

    const nextQuestion = () => {
        let index = questions.findIndex(item=>item==question);
        setQuestion(questions[++index]);
    };
    return (
        <Card style={{textAlign: 'center'}}>
            <Card.Title>
               {question.category}
            </Card.Title>
            <div style={{border: '3px solid red',width: '60vw', height: '30vw', margin: '0 auto'}}>
                {question.question}
                <div key={`true`} className="mb-3">
          <Form.Check inline
            type={'radio'}
            id={`true`}
            label={`True`}
          />
            </div> <div key={`false`} className="mb-3">
          <Form.Check 
            type={'radio'}
            id={`false`}
            label={`False`}
          />
            </div>
            </div>
        </Card>
    );
};

export default QuizPage;
