import React, {useState, useEffect} from 'react';
import { Card, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import {getQuestions} from './../../store/actions/questionActions';
import Spinner from 'react-bootstrap/Spinner';
const QuizPage = () => {
    const dispatch = useDispatch();
    const questions = useSelector(state => state.questionsReducer.questions);
    const [question, setQuestion] = useState({});
    const [choice, setChoice] = useState(null);
    const [loading, setLoading] = useState(false);
    const [score, setScore] = useState(0);
    const [index, setIndex] = useState(0);
    useEffect(() => {
       dispatch(getQuestions()); 
    }, []);

    useEffect(() => { console.log(questions);
        if(questions){
            setQuestion(questions[0]);
            setIndex(1);
        }
    }, [questions]);

    const check_answer = (user_answer) => { console.log(user_answer.toLowerCase() == question.correct_answer.toLowerCase());
        if(user_answer.toLowerCase() == question.correct_answer.toLowerCase()) {setScore(val=>++val);}
    };

    const nextQuestion = (evt) => {
        setLoading(true);
        let q_index = questions.findIndex(item=>item==question);
        check_answer(evt.target.value);
        setChoice(evt.target.value);
        setTimeout(() => {
            setChoice(null);
        setQuestion(questions[++q_index]);
        setIndex(x=>++x);
        setLoading(false);
        }, 1000);

    };
    return (
        <Card style={{textAlign: 'center', paddingTop: '20px'}}>
            <Card.Title>
               {question.category}
            </Card.Title>
            <Card.Body>
            <div style={{border: '3px solid red',width: '60vw', height: '50vh', margin: '0 auto'}}>
                {loading?<div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh'}}><Spinner animation="border" /></div>:<h3>{question?.question?.replace(/&quot;/g, '"')}</h3> }
                </div>
                <div key={`true`} className="mt-3">
          <Form.Check inline
          name="group1"
            type={'radio'}
            id={`true`}
            label={`True`}
            onClick={nextQuestion}
            value={'true'}
            checked={choice=='true'}
          />

          <Form.Check  inline
          name="group1"
            type={'radio'}
            id={`false`}
            label={`False`}
            onClick={nextQuestion}
            value={'false'}
            checked={choice=='false'}
          />
            </div>
            </Card.Body>

        </Card>
    );
};

export default QuizPage;
